package loops;

public class BreakStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Print 1 to 15 and break on 10
		// Usage of break

		for(int i=1;i<=15;i++)
		{
			if(i==10)
			{
				break;
				
			}
			System.out.println(i);
			
		}
		
		
		
	}

}
